INSERT INTO `lugares`(`nombre`, `departamento`, `foto`, `url_video`, `texto`) VALUES ('Montevideo',1,'Montevideo.jpeg','','Montevideo es la Capital de la República Oriental del Uruguay, centro político y económico del país y sede administrativa del Mercosur. Su proceso fundacional se sitúa entre 1724 y 1730.');
INSERT INTO `lugares`(`nombre`, `departamento`, `foto`, `url_video`, `texto`) VALUES ('Maldonado',1,'Maldonado.jpeg','','Maldonado, es la capital del departamento homónimo. Actualmente se encuentra conurbada con la ciudad de Punta del Este y de forma parcial está vinculada a la ciudad de San Carlos. Entre las tres ciudades conforman el segundo conurbano más amplio y poblado del país. El departamento en su conjunto es rico en contrastes.');
INSERT INTO `lugares`(`nombre`, `departamento`, `foto`, `url_video`, `texto`) VALUES ('Paysandu',1,'Paysandu.jpeg','','Paysandú es una de las ciudades más importantes del Uruguay. Es la capital del departamento homónimo con 119.429 habitantes.')
